mapper.select:
select am.*,su.s_whether from (SELECT *
FROM v_model m,
(SELECT model_code
FROM v_model_grade


        AND one_level = #{oneLevel}


        AND second_level = #{secondLevel}


        AND three_level = #{threeLevel}


) g
WHERE g.model_code = m.m_code) am LEFT JOIN v_subscribe su on am.m_id = su.model_id

    and su.custom_id = #{customId}
